"use client"

import { useState, useEffect } from "react"
import { LoaderIcon, RotateIcon, TrophyIcon, ClockIcon, StarIcon, SettingsIcon } from "../"
import { Card, CardHeader, CardContent, CardTitle, Button, Progress, Badge, Select, SelectItem } from "./ui-components"

// Mock questions as fallback (will be replaced with API data)
const mockQuestions = [
  {
    id: "1",
    question: "What is the capital of France?",
    options: ["London", "Berlin", "Paris", "Madrid"],
    correctAnswer: 2,
    difficulty: "easy",
    category: "Geography",
  },
  {
    id: "2",
    question: "Which planet is known as the Red Planet?",
    options: ["Venus", "Mars", "Jupiter", "Saturn"],
    correctAnswer: 1,
    difficulty: "easy",
    category: "Science",
  },
  {
    id: "3",
    question: "What is 15 + 27?",
    options: ["40", "41", "42", "43"],
    correctAnswer: 2,
    difficulty: "easy",
    category: "Mathematics",
  },
  {
    id: "4",
    question: "Who painted the Mona Lisa?",
    options: ["Vincent van Gogh", "Pablo Picasso", "Leonardo da Vinci", "Michelangelo"],
    correctAnswer: 2,
    difficulty: "medium",
    category: "Art",
  },
  {
    id: "5",
    question: "What is the largest ocean on Earth?",
    options: ["Atlantic Ocean", "Indian Ocean", "Arctic Ocean", "Pacific Ocean"],
    correctAnswer: 3,
    difficulty: "easy",
    category: "Geography",
  },
]

async function fetchTriviaQuestions(amount = 8, difficulty) {
  try {
    let url = `https://opentdb.com/api.php?amount=${amount}&type=multiple&encode=url3986`
    if (difficulty && difficulty !== "mixed") {
      url += `&difficulty=${difficulty}`
    }

    const response = await fetch(url, {
      method: "GET",
      headers: {
        Accept: "application/json",
      },
    })

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`)
    }

    const data = await response.json()

    // Handle different API response codes
    switch (data.response_code) {
      case 0:
        // Success
        break
      case 1:
        throw new Error("No results found. Try a different difficulty level.")
      case 2:
        throw new Error("Invalid parameter in API request.")
      case 3:
        throw new Error("Token not found.")
      case 4:
        throw new Error("Token empty.")
      default:
        throw new Error("Unknown API error occurred.")
    }

    if (!data.results || data.results.length === 0) {
      throw new Error("No questions received from API")
    }

    return data.results.map((item, index) => {
      try {
        // Decode URL-encoded strings with better error handling
        const question = decodeURIComponent(item.question)
        const correctAnswer = decodeURIComponent(item.correct_answer)
        const incorrectAnswers = item.incorrect_answers.map((ans) => decodeURIComponent(ans))

        // Randomize answer positions
        const allAnswers = [...incorrectAnswers, correctAnswer]
        const shuffledAnswers = allAnswers.sort(() => Math.random() - 0.5)
        const correctIndex = shuffledAnswers.indexOf(correctAnswer)

        return {
          id: `trivia-${Date.now()}-${index}`,
          question,
          options: shuffledAnswers,
          correctAnswer: correctIndex,
          difficulty: item.difficulty,
          category: decodeURIComponent(item.category),
        }
      } catch (decodeError) {
        console.error("Error decoding question:", decodeError)
        throw new Error("Failed to process question data")
      }
    })
  } catch (error) {
    console.error("Error fetching trivia questions:", error)
    throw error
  }
}

const getHighScores = () => {
  if (typeof window === "undefined") return []
  const scores = localStorage.getItem("quiz-high-scores")
  return scores ? JSON.parse(scores) : []
}

const saveHighScore = (score) => {
  if (typeof window === "undefined") return
  const scores = getHighScores()
  scores.push(score)
  scores.sort((a, b) => b.percentage - a.percentage)
  const topScores = scores.slice(0, 10) // Keep top 10 scores
  localStorage.setItem("quiz-high-scores", JSON.stringify(topScores))
}

export function QuizApp({ onBack }) {
  const [quizState, setQuizState] = useState({
    questions: [],
    currentQuestionIndex: 0,
    selectedAnswers: [],
    isComplete: false,
    score: 0,
    isLoading: true,
    error: null,
    timeLeft: 30,
    timerEnabled: false,
    difficulty: "mixed",
  })

  const [showSettings, setShowSettings] = useState(false)
  const [highScores, setHighScores] = useState([])

  useEffect(() => {
    let interval

    if (quizState.timerEnabled && quizState.timeLeft > 0 && !quizState.isComplete && !quizState.isLoading) {
      interval = setInterval(() => {
        setQuizState((prev) => {
          if (prev.timeLeft <= 1) {
            // Auto-advance when timer runs out
            if (prev.currentQuestionIndex < prev.questions.length - 1) {
              return {
                ...prev,
                currentQuestionIndex: prev.currentQuestionIndex + 1,
                timeLeft: 30,
              }
            } else {
              // Finish quiz if on last question
              const score = prev.selectedAnswers.reduce((total, answer, index) => {
                if (answer === prev.questions[index]?.correctAnswer) {
                  return total + 1
                }
                return total
              }, 0)

              return {
                ...prev,
                isComplete: true,
                score,
                timeLeft: 0,
              }
            }
          }
          return { ...prev, timeLeft: prev.timeLeft - 1 }
        })
      }, 1000)
    }

    return () => clearInterval(interval)
  }, [
    quizState.timerEnabled,
    quizState.timeLeft,
    quizState.isComplete,
    quizState.isLoading,
    quizState.currentQuestionIndex,
  ])

  useEffect(() => {
    setHighScores(getHighScores())
  }, [])

  // Load questions on component mount
  useEffect(() => {
    if (!showSettings) {
      loadQuestions()
    }
  }, [showSettings, quizState.difficulty])

  const loadQuestions = async () => {
    setQuizState((prev) => ({ ...prev, isLoading: true, error: null }))

    try {
      // Always try API first with enhanced error handling
      const questions = await fetchTriviaQuestions(
        8, // Load 8 questions as requested (5-10 range)
        quizState.difficulty === "mixed" ? undefined : quizState.difficulty,
      )

      if (questions.length === 0) {
        throw new Error("No questions received")
      }

      setQuizState((prev) => ({
        ...prev,
        questions,
        selectedAnswers: new Array(questions.length).fill(null),
        isLoading: false,
        currentQuestionIndex: 0,
        timeLeft: 30,
        isComplete: false,
        score: 0,
      }))
    } catch (error) {
      console.error("Failed to load questions from API:", error)

      // Enhanced fallback with user notification
      try {
        console.warn("Using fallback mock questions due to API failure")
        const fallbackQuestions = mockQuestions.slice(0, 5) // Use 5 mock questions as fallback

        setQuizState((prev) => ({
          ...prev,
          questions: fallbackQuestions,
          selectedAnswers: new Array(fallbackQuestions.length).fill(null),
          isLoading: false,
          currentQuestionIndex: 0,
          timeLeft: 30,
          isComplete: false,
          score: 0,
          error: "Using offline questions (API unavailable)",
        }))
      } catch (fallbackError) {
        setQuizState((prev) => ({
          ...prev,
          error: "Failed to load questions. Please check your internet connection and try again.",
          isLoading: false,
        }))
      }
    }
  }

  const selectAnswer = (answerIndex) => {
    if (quizState.isComplete) return

    const newSelectedAnswers = [...quizState.selectedAnswers]
    newSelectedAnswers[quizState.currentQuestionIndex] = answerIndex

    setQuizState((prev) => ({
      ...prev,
      selectedAnswers: newSelectedAnswers,
    }))
  }

  const nextQuestion = () => {
    if (quizState.currentQuestionIndex < quizState.questions.length - 1) {
      setQuizState((prev) => ({
        ...prev,
        currentQuestionIndex: prev.currentQuestionIndex + 1,
        timeLeft: 30, // Reset timer for next question
      }))
    } else {
      finishQuiz()
    }
  }

  const previousQuestion = () => {
    if (quizState.currentQuestionIndex > 0) {
      setQuizState((prev) => ({
        ...prev,
        currentQuestionIndex: prev.currentQuestionIndex - 1,
        timeLeft: 30, // Reset timer when going back
      }))
    }
  }

  const finishQuiz = () => {
    const score = quizState.selectedAnswers.reduce((total, answer, index) => {
      if (answer === quizState.questions[index]?.correctAnswer) {
        return total + 1
      }
      return total
    }, 0)

    const percentage = Math.round((score / quizState.questions.length) * 100)
    const newHighScore = {
      score,
      total: quizState.questions.length,
      percentage,
      difficulty: quizState.difficulty,
      date: new Date().toLocaleDateString(),
    }

    saveHighScore(newHighScore)
    setHighScores(getHighScores())

    setQuizState((prev) => ({
      ...prev,
      isComplete: true,
      score,
    }))
  }

  const restartQuiz = () => {
    setQuizState((prev) => ({
      ...prev,
      questions: [],
      currentQuestionIndex: 0,
      selectedAnswers: [],
      isComplete: false,
      score: 0,
      isLoading: true,
      error: null,
      timeLeft: 30,
    }))
    loadQuestions()
  }

  if (showSettings) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center p-4">
        <Card className="w-full max-w-2xl">
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center gap-3 text-primary">
                <SettingsIcon />
                Quiz Settings
              </CardTitle>
              {onBack && (
                <Button variant="ghost" size="sm" onClick={onBack} aria-label="Back to home">
                  â† Home
                </Button>
              )}
            </div>
          </CardHeader>
          <CardContent className="space-y-8">
            <div className="space-y-6">
              <div className="space-y-3">
                <label className="text-sm font-semibold mb-2 block text-card-foreground">Difficulty Level</label>
                <Select
                  value={quizState.difficulty}
                  onValueChange={(value) => setQuizState((prev) => ({ ...prev, difficulty: value }))}
                >
                  <SelectItem value="mixed">ðŸŽ¯ Mixed Difficulty</SelectItem>
                  <SelectItem value="easy">ðŸŸ¢ Easy</SelectItem>
                  <SelectItem value="medium">ðŸŸ¡ Medium</SelectItem>
                  <SelectItem value="hard">ðŸ”´ Hard</SelectItem>
                </Select>
              </div>

              <div className="flex items-center justify-between p-4 bg-muted/50 rounded-xl border border-border">
                <div>
                  <label className="text-sm font-semibold text-card-foreground">Timer Challenge</label>
                  <p className="text-xs text-muted-foreground">30 seconds per question with auto-advance</p>
                </div>
                <Button
                  variant={quizState.timerEnabled ? "default" : "outline"}
                  size="sm"
                  onClick={() => setQuizState((prev) => ({ ...prev, timerEnabled: !prev.timerEnabled }))}
                >
                  {quizState.timerEnabled ? "â° Enabled" : "â¸ï¸ Disabled"}
                </Button>
              </div>
            </div>

            {highScores.length > 0 && (
              <div className="space-y-4">
                <h3 className="font-bold text-lg flex items-center gap-2 text-card-foreground">
                  <StarIcon />
                  High Scores
                </h3>
                <div className="space-y-3 max-h-48 overflow-y-auto">
                  {highScores.map((score, index) => (
                    <div
                      key={index}
                      className="flex justify-between items-center p-4 bg-card/50 rounded-xl border border-border shadow-sm hover:shadow-md transition-all duration-200"
                    >
                      <div>
                        <span className="font-bold text-primary">
                          {score.score}/{score.total}
                        </span>
                        <span className="text-sm text-muted-foreground ml-2">
                          ({score.percentage}%) - {score.difficulty}
                        </span>
                      </div>
                      <span className="text-xs text-muted-foreground bg-muted px-2 py-1 rounded-full">
                        {score.date}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="flex gap-4">
              <Button onClick={() => setShowSettings(false)} className="flex-1" size="lg">
                ðŸš€ Start Quiz
              </Button>
              <Button variant="outline" onClick={() => setShowSettings(false)} size="lg">
                Cancel
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    )
  }

  if (quizState.isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center">
        <Card className="w-full max-w-md">
          {onBack && (
            <CardHeader className="pb-2">
              <Button variant="ghost" size="sm" onClick={onBack} className="w-fit" aria-label="Back to home">
                â† Home
              </Button>
            </CardHeader>
          )}
          <CardContent className="flex flex-col items-center justify-center p-12">
            <LoaderIcon />
            <p className="text-card-foreground text-center mt-6 font-semibold">
              Loading quiz questions from Open Trivia DB...
            </p>
            <p className="text-xs text-muted-foreground mt-3 text-center bg-muted px-3 py-1 rounded-full">
              Difficulty: {quizState.difficulty === "mixed" ? "Mixed" : quizState.difficulty}
            </p>
          </CardContent>
        </Card>
      </div>
    )
  }

  if (quizState.error) {
    const isOfflineMode = quizState.error.includes("offline questions")

    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center p-4">
        <Card className="w-full max-w-md">
          {onBack && (
            <CardHeader className="pb-2">
              <Button variant="ghost" size="sm" onClick={onBack} className="w-fit" aria-label="Back to home">
                â† Home
              </Button>
            </CardHeader>
          )}
          <CardContent className="flex flex-col items-center justify-center p-8">
            <div
              className={`p-6 rounded-xl mb-6 text-center border-2 ${isOfflineMode ? "bg-secondary/10 border-secondary/30" : "bg-destructive/10 border-destructive/30"}`}
            >
              <p className={`${isOfflineMode ? "text-secondary" : "text-destructive"} font-bold mb-3 text-lg`}>
                {isOfflineMode ? "âš ï¸ Offline Mode" : "âŒ Connection Error"}
              </p>
              <p className={`${isOfflineMode ? "text-secondary/80" : "text-destructive/80"} text-sm`}>
                {quizState.error}
              </p>
            </div>
            <div className="flex gap-3">
              <Button onClick={loadQuestions} variant="outline">
                {isOfflineMode ? "ðŸ”„ Retry Online" : "ðŸ”„ Try Again"}
              </Button>
              <Button onClick={() => setShowSettings(true)} variant="outline">
                âš™ï¸ Settings
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    )
  }

  // Results view
  if (quizState.isComplete) {
    const percentage = Math.round((quizState.score / quizState.questions.length) * 100)

    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center p-4">
        <Card className="w-full max-w-3xl">
          <CardHeader className="text-center">
            {onBack && (
              <div className="flex justify-start mb-4">
                <Button variant="ghost" size="sm" onClick={onBack} aria-label="Back to home">
                  â† Home
                </Button>
              </div>
            )}
            <div className="flex justify-center mb-6">
              <TrophyIcon />
            </div>
            <CardTitle className="text-3xl font-bold text-primary mb-4">ðŸŽ‰ Quiz Complete!</CardTitle>
            <Badge variant="secondary" className="mx-auto text-sm px-4 py-2">
              {quizState.difficulty} difficulty
            </Badge>
          </CardHeader>
          <CardContent className="space-y-8">
            <div className="text-center p-6 bg-gradient-to-r from-primary/10 to-secondary/10 rounded-xl border border-primary/20">
              <div className="text-5xl font-bold text-primary mb-3">
                {quizState.score}/{quizState.questions.length}
              </div>
              <p className="text-card-foreground text-lg">
                You scored <span className="font-bold text-primary">{percentage}%</span> on this quiz
              </p>
            </div>

            <div className="space-y-4">
              <h3 className="font-bold text-xl text-card-foreground">ðŸ“ Review Your Answers:</h3>
              {quizState.questions.map((question, index) => {
                const userAnswer = quizState.selectedAnswers[index]
                const isCorrect = userAnswer === question.correctAnswer

                return (
                  <div
                    key={question.id}
                    className="border-2 border-border rounded-xl p-6 bg-card/50 hover:bg-card/70 transition-all duration-200"
                  >
                    <div className="flex justify-between items-start mb-4">
                      <p className="font-semibold flex-1 text-card-foreground">
                        {index + 1}. {question.question}
                      </p>
                      {question.difficulty && (
                        <Badge variant="outline" className="ml-3">
                          {question.difficulty}
                        </Badge>
                      )}
                    </div>
                    <div className="space-y-2 text-sm">
                      <p className={`font-semibold ${isCorrect ? "text-primary" : "text-destructive"}`}>
                        Your answer: {userAnswer !== null ? question.options[userAnswer] : "No answer"}
                        {isCorrect ? " âœ…" : " âŒ"}
                      </p>
                      {!isCorrect && (
                        <p className="text-primary font-medium">
                          Correct answer: {question.options[question.correctAnswer]} âœ…
                        </p>
                      )}
                    </div>
                  </div>
                )
              })}
            </div>

            <div className="flex gap-4 justify-center">
              <Button onClick={restartQuiz} className="flex items-center gap-2" size="lg">
                <RotateIcon />ðŸ”„ Take Quiz Again
              </Button>
              <Button onClick={() => setShowSettings(true)} variant="outline" size="lg">
                âš™ï¸ Settings
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    )
  }

  // Quiz view
  const currentQuestion = quizState.questions[quizState.currentQuestionIndex]
  const selectedAnswer = quizState.selectedAnswers[quizState.currentQuestionIndex]
  const progress = ((quizState.currentQuestionIndex + 1) / quizState.questions.length) * 100

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-muted/30 to-background flex items-center justify-center p-4">
      <Card className="w-full max-w-3xl">
        <CardHeader>
          <div className="flex justify-between items-center mb-6">
            <div className="flex items-center gap-4">
              <CardTitle className="text-2xl font-bold text-primary">ðŸ§  Quiz Challenge</CardTitle>
              {onBack && (
                <Button variant="ghost" size="sm" onClick={onBack} aria-label="Back to home">
                  â† Home
                </Button>
              )}
            </div>
            <div className="flex items-center gap-4">
              {quizState.timerEnabled && (
                <div
                  className={`flex items-center gap-2 text-sm px-3 py-2 rounded-full border-2 ${quizState.timeLeft <= 10 ? "text-destructive border-destructive/30 bg-destructive/10 animate-pulse" : "text-card-foreground border-border bg-card/50"}`}
                >
                  <ClockIcon />
                  <span className="font-bold">{quizState.timeLeft}s</span>
                </div>
              )}
              <span className="text-sm text-muted-foreground bg-muted px-3 py-2 rounded-full font-medium">
                Question {quizState.currentQuestionIndex + 1} of {quizState.questions.length}
              </span>
              <Button variant="ghost" size="sm" onClick={() => setShowSettings(true)} aria-label="Open settings">
                <SettingsIcon />
              </Button>
            </div>
          </div>
          <Progress value={progress} className="w-full" />
        </CardHeader>

        <CardContent className="space-y-8">
          <div key={quizState.currentQuestionIndex} className="animate-slide-in">
            <div className="flex justify-between items-start mb-6">
              <h2 className="text-xl font-bold flex-1 text-card-foreground leading-relaxed">
                {currentQuestion?.question}
              </h2>
              {currentQuestion?.difficulty && (
                <Badge variant="outline" className="ml-4">
                  {currentQuestion.difficulty}
                </Badge>
              )}
            </div>

            <div className="grid gap-4">
              {currentQuestion?.options.map((option, index) => (
                <Button
                  key={index}
                  variant={selectedAnswer === index ? "default" : "outline"}
                  className={`justify-start text-left h-auto p-6 text-wrap transition-all duration-300 hover:scale-[1.02] active:scale-[0.98] ${selectedAnswer === index ? "ring-2 ring-primary/50" : ""}`}
                  onClick={() => selectAnswer(index)}
                  aria-label={`Option ${String.fromCharCode(65 + index)}: ${option}`}
                >
                  <span className="font-bold mr-4 text-lg">{String.fromCharCode(65 + index)}.</span>
                  <span className="font-medium">{option}</span>
                </Button>
              ))}
            </div>
          </div>

          <div className="flex justify-between">
            <Button
              variant="outline"
              onClick={previousQuestion}
              disabled={quizState.currentQuestionIndex === 0}
              aria-label="Previous question"
              size="lg"
            >
              â† Previous
            </Button>

            <Button
              onClick={nextQuestion}
              disabled={selectedAnswer === null}
              className="flex items-center gap-2"
              aria-label={
                quizState.currentQuestionIndex === quizState.questions.length - 1 ? "Finish quiz" : "Next question"
              }
              size="lg"
            >
              {quizState.currentQuestionIndex === quizState.questions.length - 1 ? "ðŸ Finish" : "Next â†’"}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}
