import { useState, useEffect } from "react"
import { LoaderIcon, RotateIcon, TrophyIcon, ClockIcon, StarIcon, SettingsIcon } from "./Icons"
import { Card, CardHeader, CardContent, CardTitle, Button, Progress, Badge, Select, SelectItem } from "./Ui-components"


async function fetchTriviaQuestions(amount = 8, difficulty) {
    try {
        let url = `https://opentdb.com/api.php?amount=${amount}&type=multiple&encode=url3986`
        if (difficulty && difficulty !== "mixed") {
            url += `&difficulty=${difficulty}`
        }

        const response = await fetch(url, {
            method: "GET",
            headers: {
                Accept: "application/json",
            },
        })

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`)
        }

        const data = await response.json()

        if (!data.results || data.results.length === 0) {
            throw new Error("No questions received from API")
        }

        return data.results.map((item, index) => {
            try {
                // Decode URL-encoded strings with better error handling
                const question = decodeURIComponent(item.question)
                const correctAnswer = decodeURIComponent(item.correct_answer)
                const incorrectAnswers = item.incorrect_answers.map((ans) => decodeURIComponent(ans))

                // Randomize answer positions
                const allAnswers = [...incorrectAnswers, correctAnswer]
                const shuffledAnswers = allAnswers.sort(() => Math.random() - 0.5)
                const correctIndex = shuffledAnswers.indexOf(correctAnswer)

                return {
                    id: `trivia-${Date.now()}-${index}`,
                    question,
                    options: shuffledAnswers,
                    correctAnswer: correctIndex,
                    difficulty: item.difficulty,
                    category: decodeURIComponent(item.category),
                }
            } catch (decodeError) {
                console.error("Error decoding question:", decodeError)
                throw new Error("Failed to process question data")
            }
        })
    } catch (error) {
        console.error("Error fetching trivia questions:", error)
        throw error
    }
}

const getHighScores = () => {
    if (typeof window === "undefined") return []
    const scores = localStorage.getItem("quiz-high-scores")
    return scores ? JSON.parse(scores) : []
}

const saveHighScore = (score) => {
    if (typeof window === "undefined") return
    const scores = getHighScores()
    scores.push(score)
    scores.sort((a, b) => b.percentage - a.percentage)
    const topScores = scores.slice(0, 10) // Keep top 10 scores
    localStorage.setItem("quiz-high-scores", JSON.stringify(topScores))
}

export function QuizApp({ onBack }) {

    const [quizState, setQuizState] = useState({
        questions: [],
        currentQuestionIndex: 0,
        selectedAnswers: [],
        isComplete: false,
        score: 0,
        isLoading: true,
        error: null,
        timeLeft: 30,
        timerEnabled: false,
        difficulty: "mixed",
    })

    const [showSettings, setShowSettings] = useState(false)
    const [highScores, setHighScores] = useState([])

    // Timer effect remains same
    useEffect(() => {
        let interval
        if (quizState.timerEnabled && quizState.timeLeft > 0 && !quizState.isComplete && !quizState.isLoading) {
            interval = setInterval(() => {
                setQuizState((prev) => {
                    if (prev.timeLeft <= 1) {
                        if (prev.currentQuestionIndex < prev.questions.length - 1) {
                            return {
                                ...prev,
                                currentQuestionIndex: prev.currentQuestionIndex + 1,
                                timeLeft: 30,
                            }
                        } else {
                            const score = prev.selectedAnswers.reduce((total, answer, index) => {
                                if (answer === prev.questions[index]?.correctAnswer) return total + 1
                                return total
                            }, 0)
                            return { ...prev, isComplete: true, score, timeLeft: 0 }
                        }
                    }
                    return { ...prev, timeLeft: prev.timeLeft - 1 }
                })
            }, 1000)
        }
        return () => clearInterval(interval)
    }, [quizState.timerEnabled, quizState.timeLeft, quizState.isComplete, quizState.isLoading, quizState.currentQuestionIndex])

    useEffect(() => { setHighScores(getHighScores()) }, [])

    useEffect(() => {
        if (!showSettings) loadQuestions()
    }, [showSettings, quizState.difficulty])

    const loadQuestions = async () => {
        setQuizState((prev) => ({ ...prev, isLoading: true, error: null }))
        try {
            const questions = await fetchTriviaQuestions(8, quizState.difficulty === "mixed" ? undefined : quizState.difficulty)
            setQuizState((prev) => ({
                ...prev,
                questions,
                selectedAnswers: new Array(questions.length).fill(null),
                isLoading: false,
                currentQuestionIndex: 0,
                timeLeft: 30,
                isComplete: false,
                score: 0,
            }))
        } catch (error) { console.error(error) }
    }

    const selectAnswer = (answerIndex) => {
        if (quizState.isComplete) return
        const newSelectedAnswers = [...quizState.selectedAnswers]
        newSelectedAnswers[quizState.currentQuestionIndex] = answerIndex
        setQuizState((prev) => ({ ...prev, selectedAnswers: newSelectedAnswers }))
    }

    const nextQuestion = () => {
        if (quizState.currentQuestionIndex < quizState.questions.length - 1)
            setQuizState((prev) => ({ ...prev, currentQuestionIndex: prev.currentQuestionIndex + 1, timeLeft: 30 }))
        else finishQuiz()
    }

    const previousQuestion = () => {
        if (quizState.currentQuestionIndex > 0)
            setQuizState((prev) => ({ ...prev, currentQuestionIndex: prev.currentQuestionIndex - 1, timeLeft: 30 }))
    }

    const finishQuiz = () => {
        const score = quizState.selectedAnswers.reduce((total, answer, index) => {
            if (answer === quizState.questions[index]?.correctAnswer) return total + 1
            return total
        }, 0)
        const percentage = Math.round((score / quizState.questions.length) * 100)
        saveHighScore({ score, total: quizState.questions.length, percentage, difficulty: quizState.difficulty, date: new Date().toLocaleDateString() })
        setHighScores(getHighScores())
        setQuizState((prev) => ({ ...prev, isComplete: true, score }))
    }

    const restartQuiz = () => {
        setQuizState((prev) => ({ ...prev, questions: [], currentQuestionIndex: 0, selectedAnswers: [], isComplete: false, score: 0, isLoading: true, error: null, timeLeft: 30 }))
        loadQuestions()
    }

    // ðŸŒˆ Settings Page
    if (showSettings) {
        return (
            <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-br from-purple-500 via-indigo-500 to-blue-500">
                <Card className="w-full max-w-2xl backdrop-blur-md bg-white/30 border border-white/20 shadow-lg">
                    <CardHeader>
                        <CardTitle className="flex items-center gap-2 text-white text-2xl">
                            <SettingsIcon />
                            Quiz Settings
                        </CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-6 text-white">
                        <div className="space-y-4">
                            <div>
                                <label className="text-sm font-semibold mb-2 block">Difficulty Level</label>
                                <Select value={quizState.difficulty} onValueChange={(value) => setQuizState((prev) => ({ ...prev, difficulty: value }))}>
                                    <SelectItem value="mixed">Mixed</SelectItem>
                                    <SelectItem value="easy">Easy</SelectItem>
                                    <SelectItem value="medium">Medium</SelectItem>
                                    <SelectItem value="hard">Hard</SelectItem>
                                </Select>
                            </div>
                            <div className="flex items-center justify-between">
                                <div>
                                    <label className="text-sm font-semibold">Timer (30s per question)</label>
                                    <p className="text-xs text-white/80">Auto-advance when time runs out</p>
                                </div>
                                <Button variant={quizState.timerEnabled ? "default" : "outline"} size="sm" onClick={() => setQuizState((prev) => ({ ...prev, timerEnabled: !prev.timerEnabled }))}>
                                    {quizState.timerEnabled ? "Enabled" : "Disabled"}
                                </Button>
                            </div>
                        </div>

                        {highScores.length > 0 && (
                            <div className="space-y-4">
                                <h3 className="font-semibold flex items-center gap-2 text-white">
                                    <StarIcon />
                                    High Scores
                                </h3>
                                <div className="space-y-2 max-h-48 overflow-y-auto">
                                    {highScores.map((score, index) => (
                                        <div key={index} className="flex justify-between items-center p-2 bg-white/20 rounded">
                                            <div>
                                                <span className="font-medium">{score.score}/{score.total}</span>
                                                <span className="text-sm text-white/80 ml-2">({score.percentage}%) - {score.difficulty}</span>
                                            </div>
                                            <span className="text-xs text-white/60">{score.date}</span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        <div className="flex gap-3">
                            <Button onClick={() => setShowSettings(false)} className="flex-1 bg-indigo-600 hover:bg-indigo-700 text-white">Start Quiz</Button>
                            <Button variant="outline" onClick={() => setShowSettings(false)} className="text-white border-white/60 hover:bg-white/10">Cancel</Button>
                        </div>
                    </CardContent>
                </Card>
            </div>
        )
    }

    // ðŸŒˆ Loading screen
    if (quizState.isLoading) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-400 via-pink-400 to-yellow-300">
                <Card className="w-full max-w-md bg-white/80 backdrop-blur-md shadow-lg">
                    <CardContent className="flex flex-col items-center justify-center p-8 text-gray-800">
                        <LoaderIcon className="animate-spin text-indigo-600" />
                        <p className="mt-4 font-medium">Loading quiz questions...</p>
                        <p className="text-xs text-gray-600 mt-2 text-center">
                            Difficulty: {quizState.difficulty}
                        </p>
                    </CardContent>
                </Card>
            </div>
        )
    }

    // ðŸŒˆ Quiz question view
    const currentQuestion = quizState.questions[quizState.currentQuestionIndex]
    const selectedAnswer = quizState.selectedAnswers[quizState.currentQuestionIndex]
    const progress = ((quizState.currentQuestionIndex + 1) / quizState.questions.length) * 100

    return (
        <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-br from-purple-400 via-indigo-500 to-blue-500">
            <Card className="w-full max-w-2xl bg-white/80 backdrop-blur-md shadow-xl border border-white/20">
                <CardHeader>
                    <div className="flex justify-between items-center mb-4">
                        <CardTitle className="text-xl font-bold text-gray-800">Quiz App</CardTitle>
                        <div className="flex items-center gap-3">
                            {quizState.timerEnabled && (
                                <div className={`flex items-center gap-1 text-sm font-semibold ${quizState.timeLeft <= 10 ? "text-red-600 animate-pulse" : "text-gray-700"}`}>
                                    <ClockIcon /> {quizState.timeLeft}s
                                </div>
                            )}
                            <span className="text-sm text-gray-700">
                                Question {quizState.currentQuestionIndex + 1} of {quizState.questions.length}
                            </span>
                            <Button variant="ghost" size="sm" onClick={() => setShowSettings(true)}>
                                <SettingsIcon />
                            </Button>
                        </div>
                    </div>
                    <Progress value={progress} className="w-full bg-gray-200 rounded-full h-2" />
                </CardHeader>

                <CardContent className="space-y-6">
                    <h2 className="text-lg font-semibold text-gray-800">{currentQuestion?.question}</h2>
                    <div className="grid gap-3">
                        {currentQuestion?.options.map((option, index) => (
                            <Button
                                key={index}
                                variant={selectedAnswer === index ? "default" : "outline"}
                                className={`text-left h-auto p-4 rounded-lg transition-all duration-200 
                                    ${selectedAnswer === index 
                                        ? "bg-gradient-to-r from-indigo-500 to-purple-500 text-white" 
                                        : "bg-white hover:bg-indigo-50 border border-gray-300"}`
                                }
                                onClick={() => selectAnswer(index)}
                            >
                                <span className="font-medium mr-3">{String.fromCharCode(65 + index)}.</span>
                                {option}
                            </Button>
                        ))}
                    </div>

                    <div className="flex justify-between">
                        <Button variant="outline" onClick={previousQuestion} disabled={quizState.currentQuestionIndex === 0}>Previous</Button>
                        <Button onClick={nextQuestion} disabled={selectedAnswer === null}>
                            {quizState.currentQuestionIndex === quizState.questions.length - 1 ? "Finish" : "Next"}
                        </Button>
                    </div>
                </CardContent>
            </Card>
        </div>
    )
}
