"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Progress } from "@/components/ui/progress"
import { Badge } from "@/components/ui/badge"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Loader2, RotateCcw, Trophy, Clock, Star, Settings } from "lucide-react"

// Mock questions as fallback (will be replaced with API data)
const mockQuestions = [
  {
    id: "1",
    question: "What is the capital of France?",
    options: ["London", "Berlin", "Paris", "Madrid"],
    correctAnswer: 2,
    difficulty: "easy",
    category: "Geography",
  },
  {
    id: "2",
    question: "Which planet is known as the Red Planet?",
    options: ["Venus", "Mars", "Jupiter", "Saturn"],
    correctAnswer: 1,
    difficulty: "easy",
    category: "Science",
  },
  {
    id: "3",
    question: "What is 15 + 27?",
    options: ["40", "41", "42", "43"],
    correctAnswer: 2,
    difficulty: "easy",
    category: "Mathematics",
  },
  {
    id: "4",
    question: "Who painted the Mona Lisa?",
    options: ["Vincent van Gogh", "Pablo Picasso", "Leonardo da Vinci", "Michelangelo"],
    correctAnswer: 2,
    difficulty: "medium",
    category: "Art",
  },
  {
    id: "5",
    question: "What is the largest ocean on Earth?",
    options: ["Atlantic Ocean", "Indian Ocean", "Arctic Ocean", "Pacific Ocean"],
    correctAnswer: 3,
    difficulty: "easy",
    category: "Geography",
  },
]

async function fetchTriviaQuestions(amount = 8, difficulty) {
  try {
    let url = `https://opentdb.com/api.php?amount=${amount}&type=multiple&encode=url3986`
    if (difficulty && difficulty !== "mixed") {
      url += `&difficulty=${difficulty}`
    }

    const response = await fetch(url, {
      method: "GET",
      headers: {
        Accept: "application/json",
      },
    })

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`)
    }

    const data = await response.json()

    // Handle different API response codes
    switch (data.response_code) {
      case 0:
        // Success
        break
      case 1:
        throw new Error("No results found. Try a different difficulty level.")
      case 2:
        throw new Error("Invalid parameter in API request.")
      case 3:
        throw new Error("Token not found.")
      case 4:
        throw new Error("Token empty.")
      default:
        throw new Error("Unknown API error occurred.")
    }

    if (!data.results || data.results.length === 0) {
      throw new Error("No questions received from API")
    }

    return data.results.map((item, index) => {
      try {
        // Decode URL-encoded strings with better error handling
        const question = decodeURIComponent(item.question)
        const correctAnswer = decodeURIComponent(item.correct_answer)
        const incorrectAnswers = item.incorrect_answers.map((ans) => decodeURIComponent(ans))

        // Randomize answer positions
        const allAnswers = [...incorrectAnswers, correctAnswer]
        const shuffledAnswers = allAnswers.sort(() => Math.random() - 0.5)
        const correctIndex = shuffledAnswers.indexOf(correctAnswer)

        return {
          id: `trivia-${Date.now()}-${index}`,
          question,
          options: shuffledAnswers,
          correctAnswer: correctIndex,
          difficulty: item.difficulty,
          category: decodeURIComponent(item.category),
        }
      } catch (decodeError) {
        console.error("Error decoding question:", decodeError)
        throw new Error("Failed to process question data")
      }
    })
  } catch (error) {
    console.error("Error fetching trivia questions:", error)
    throw error
  }
}

const getHighScores = () => {
  if (typeof window === "undefined") return []
  const scores = localStorage.getItem("quiz-high-scores")
  return scores ? JSON.parse(scores) : []
}

const saveHighScore = (score) => {
  if (typeof window === "undefined") return
  const scores = getHighScores()
  scores.push(score)
  scores.sort((a, b) => b.percentage - a.percentage)
  const topScores = scores.slice(0, 10) // Keep top 10 scores
  localStorage.setItem("quiz-high-scores", JSON.stringify(topScores))
}

export function QuizApp() {
  const [quizState, setQuizState] = useState({
    questions: [],
    currentQuestionIndex: 0,
    selectedAnswers: [],
    isComplete: false,
    score: 0,
    isLoading: true,
    error: null,
    timeLeft: 30,
    timerEnabled: false,
    difficulty: "mixed",
  })

  const [showSettings, setShowSettings] = useState(false)
  const [highScores, setHighScores] = useState([])

  useEffect(() => {
    let interval

    if (quizState.timerEnabled && quizState.timeLeft > 0 && !quizState.isComplete && !quizState.isLoading) {
      interval = setInterval(() => {
        setQuizState((prev) => {
          if (prev.timeLeft <= 1) {
            // Auto-advance when timer runs out
            if (prev.currentQuestionIndex < prev.questions.length - 1) {
              return {
                ...prev,
                currentQuestionIndex: prev.currentQuestionIndex + 1,
                timeLeft: 30,
              }
            } else {
              // Finish quiz if on last question
              const score = prev.selectedAnswers.reduce((total, answer, index) => {
                if (answer === prev.questions[index]?.correctAnswer) {
                  return total + 1
                }
                return total
              }, 0)

              return {
                ...prev,
                isComplete: true,
                score,
                timeLeft: 0,
              }
            }
          }
          return { ...prev, timeLeft: prev.timeLeft - 1 }
        })
      }, 1000)
    }

    return () => clearInterval(interval)
  }, [
    quizState.timerEnabled,
    quizState.timeLeft,
    quizState.isComplete,
    quizState.isLoading,
    quizState.currentQuestionIndex,
  ])

  useEffect(() => {
    setHighScores(getHighScores())
  }, [])

  // Load questions on component mount
  useEffect(() => {
    if (!showSettings) {
      loadQuestions()
    }
  }, [showSettings, quizState.difficulty])

  const loadQuestions = async () => {
    setQuizState((prev) => ({ ...prev, isLoading: true, error: null }))

    try {
      // Always try API first with enhanced error handling
      const questions = await fetchTriviaQuestions(
        8, // Load 8 questions as requested (5-10 range)
        quizState.difficulty === "mixed" ? undefined : quizState.difficulty,
      )

      if (questions.length === 0) {
        throw new Error("No questions received")
      }

      setQuizState((prev) => ({
        ...prev,
        questions,
        selectedAnswers: new Array(questions.length).fill(null),
        isLoading: false,
        currentQuestionIndex: 0,
        timeLeft: 30,
        isComplete: false,
        score: 0,
      }))
    } catch (error) {
      console.error("Failed to load questions from API:", error)

      // Enhanced fallback with user notification
      try {
        console.warn("Using fallback mock questions due to API failure")
        const fallbackQuestions = mockQuestions.slice(0, 5) // Use 5 mock questions as fallback

        setQuizState((prev) => ({
          ...prev,
          questions: fallbackQuestions,
          selectedAnswers: new Array(fallbackQuestions.length).fill(null),
          isLoading: false,
          currentQuestionIndex: 0,
          timeLeft: 30,
          isComplete: false,
          score: 0,
          error: "Using offline questions (API unavailable)",
        }))
      } catch (fallbackError) {
        setQuizState((prev) => ({
          ...prev,
          error: "Failed to load questions. Please check your internet connection and try again.",
          isLoading: false,
        }))
      }
    }
  }

  const selectAnswer = (answerIndex) => {
    if (quizState.isComplete) return

    const newSelectedAnswers = [...quizState.selectedAnswers]
    newSelectedAnswers[quizState.currentQuestionIndex] = answerIndex

    setQuizState((prev) => ({
      ...prev,
      selectedAnswers: newSelectedAnswers,
    }))
  }

  const nextQuestion = () => {
    if (quizState.currentQuestionIndex < quizState.questions.length - 1) {
      setQuizState((prev) => ({
        ...prev,
        currentQuestionIndex: prev.currentQuestionIndex + 1,
        timeLeft: 30, // Reset timer for next question
      }))
    } else {
      finishQuiz()
    }
  }

  const previousQuestion = () => {
    if (quizState.currentQuestionIndex > 0) {
      setQuizState((prev) => ({
        ...prev,
        currentQuestionIndex: prev.currentQuestionIndex - 1,
        timeLeft: 30, // Reset timer when going back
      }))
    }
  }

  const finishQuiz = () => {
    const score = quizState.selectedAnswers.reduce((total, answer, index) => {
      if (answer === quizState.questions[index]?.correctAnswer) {
        return total + 1
      }
      return total
    }, 0)

    const percentage = Math.round((score / quizState.questions.length) * 100)
    const newHighScore = {
      score,
      total: quizState.questions.length,
      percentage,
      difficulty: quizState.difficulty,
      date: new Date().toLocaleDateString(),
    }

    saveHighScore(newHighScore)
    setHighScores(getHighScores())

    setQuizState((prev) => ({
      ...prev,
      isComplete: true,
      score,
    }))
  }

  const restartQuiz = () => {
    setQuizState((prev) => ({
      ...prev,
      questions: [],
      currentQuestionIndex: 0,
      selectedAnswers: [],
      isComplete: false,
      score: 0,
      isLoading: true,
      error: null,
      timeLeft: 30,
    }))
    loadQuestions()
  }

  if (showSettings) {
    return (
      <div className="min-h-screen flex items-center justify-center p-4">
        <Card className="w-full max-w-2xl">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Settings className="h-5 w-5" />
              Quiz Settings
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="space-y-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Difficulty Level</label>
                <Select
                  value={quizState.difficulty}
                  onValueChange={(value) => setQuizState((prev) => ({ ...prev, difficulty: value }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="mixed">Mixed Difficulty</SelectItem>
                    <SelectItem value="easy">Easy</SelectItem>
                    <SelectItem value="medium">Medium</SelectItem>
                    <SelectItem value="hard">Hard</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <label className="text-sm font-medium">Timer (30 seconds per question)</label>
                  <p className="text-xs text-muted-foreground">Auto-advance when time runs out</p>
                </div>
                <Button
                  variant={quizState.timerEnabled ? "default" : "outline"}
                  size="sm"
                  onClick={() => setQuizState((prev) => ({ ...prev, timerEnabled: !prev.timerEnabled }))}
                >
                  {quizState.timerEnabled ? "Enabled" : "Disabled"}
                </Button>
              </div>
            </div>

            {highScores.length > 0 && (
              <div className="space-y-4">
                <h3 className="font-semibold flex items-center gap-2">
                  <Star className="h-4 w-4 text-primary" />
                  High Scores
                </h3>
                <div className="space-y-2 max-h-48 overflow-y-auto">
                  {highScores.map((score, index) => (
                    <div key={index} className="flex justify-between items-center p-2 bg-muted rounded">
                      <div>
                        <span className="font-medium">
                          {score.score}/{score.total}
                        </span>
                        <span className="text-sm text-muted-foreground ml-2">
                          ({score.percentage}%) - {score.difficulty}
                        </span>
                      </div>
                      <span className="text-xs text-muted-foreground">{score.date}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="flex gap-3">
              <Button onClick={() => setShowSettings(false)} className="flex-1">
                Start Quiz
              </Button>
              <Button variant="outline" onClick={() => setShowSettings(false)}>
                Cancel
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    )
  }

  if (quizState.isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Card className="w-full max-w-md">
          <CardContent className="flex flex-col items-center justify-center p-8">
            <Loader2 className="h-8 w-8 animate-spin text-primary mb-4" />
            <p className="text-muted-foreground text-center">Loading quiz questions from Open Trivia DB...</p>
            <p className="text-xs text-muted-foreground mt-2 text-center">
              Difficulty: {quizState.difficulty === "mixed" ? "Mixed" : quizState.difficulty}
            </p>
          </CardContent>
        </Card>
      </div>
    )
  }

  if (quizState.error) {
    const isOfflineMode = quizState.error.includes("offline questions")

    return (
      <div className="min-h-screen flex items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardContent className="flex flex-col items-center justify-center p-8">
            <div
              className={`p-4 rounded-lg mb-4 text-center ${isOfflineMode ? "bg-yellow-50 border border-yellow-200" : "bg-red-50 border border-red-200"}`}
            >
              <p className={`${isOfflineMode ? "text-yellow-800" : "text-red-800"} font-medium mb-2`}>
                {isOfflineMode ? "⚠️ Offline Mode" : "❌ Connection Error"}
              </p>
              <p className={`${isOfflineMode ? "text-yellow-700" : "text-red-700"} text-sm`}>{quizState.error}</p>
            </div>
            <div className="flex gap-2">
              <Button onClick={loadQuestions} variant="outline">
                {isOfflineMode ? "Retry Online" : "Try Again"}
              </Button>
              <Button onClick={() => setShowSettings(true)} variant="outline">
                Settings
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    )
  }

  // Results view
  if (quizState.isComplete) {
    const percentage = Math.round((quizState.score / quizState.questions.length) * 100)

    return (
      <div className="min-h-screen flex items-center justify-center p-4">
        <Card className="w-full max-w-2xl animate-in fade-in duration-500">
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <Trophy className="h-12 w-12 text-primary animate-bounce" />
            </div>
            <CardTitle className="text-2xl font-bold">Quiz Complete!</CardTitle>
            <Badge variant="secondary" className="mx-auto">
              {quizState.difficulty} difficulty
            </Badge>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="text-center">
              <div className="text-4xl font-bold text-primary mb-2 animate-in zoom-in duration-700">
                {quizState.score}/{quizState.questions.length}
              </div>
              <p className="text-muted-foreground">You scored {percentage}% on this quiz</p>
            </div>

            <div className="space-y-4">
              <h3 className="font-semibold text-lg">Review Your Answers:</h3>
              {quizState.questions.map((question, index) => {
                const userAnswer = quizState.selectedAnswers[index]
                const isCorrect = userAnswer === question.correctAnswer

                return (
                  <div
                    key={question.id}
                    className="border rounded-lg p-4 animate-in slide-in-from-left duration-300"
                    style={{ animationDelay: `${index * 100}ms` }}
                  >
                    <div className="flex justify-between items-start mb-2">
                      <p className="font-medium flex-1">
                        {index + 1}. {question.question}
                      </p>
                      {question.difficulty && (
                        <Badge variant="outline" className="ml-2 text-xs">
                          {question.difficulty}
                        </Badge>
                      )}
                    </div>
                    <div className="space-y-1 text-sm">
                      <p className={isCorrect ? "text-primary" : "text-destructive"}>
                        Your answer: {userAnswer !== null ? question.options[userAnswer] : "No answer"}
                        {isCorrect ? " ✓" : " ✗"}
                      </p>
                      {!isCorrect && (
                        <p className="text-primary">Correct answer: {question.options[question.correctAnswer]}</p>
                      )}
                    </div>
                  </div>
                )
              })}
            </div>

            <div className="flex gap-3 justify-center">
              <Button onClick={restartQuiz} className="flex items-center gap-2">
                <RotateCcw className="h-4 w-4" />
                Take Quiz Again
              </Button>
              <Button onClick={() => setShowSettings(true)} variant="outline">
                Settings
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    )
  }

  // Quiz view
  const currentQuestion = quizState.questions[quizState.currentQuestionIndex]
  const selectedAnswer = quizState.selectedAnswers[quizState.currentQuestionIndex]
  const progress = ((quizState.currentQuestionIndex + 1) / quizState.questions.length) * 100

  return (
    <div className="min-h-screen flex items-center justify-center p-4">
      <Card className="w-full max-w-2xl animate-in fade-in duration-300">
        <CardHeader>
          <div className="flex justify-between items-center mb-4">
            <CardTitle className="text-xl font-bold">Quiz App</CardTitle>
            <div className="flex items-center gap-3">
              {quizState.timerEnabled && (
                <div className="flex items-center gap-1 text-sm">
                  <Clock className="h-4 w-4" />
                  <span className={quizState.timeLeft <= 10 ? "text-destructive font-bold" : "text-muted-foreground"}>
                    {quizState.timeLeft}s
                  </span>
                </div>
              )}
              <span className="text-sm text-muted-foreground">
                Question {quizState.currentQuestionIndex + 1} of {quizState.questions.length}
              </span>
              <Button variant="ghost" size="sm" onClick={() => setShowSettings(true)} aria-label="Open settings">
                <Settings className="h-4 w-4" />
              </Button>
            </div>
          </div>
          <Progress value={progress} className="w-full" />
        </CardHeader>

        <CardContent className="space-y-6">
          <div className="animate-in slide-in-from-right duration-300" key={quizState.currentQuestionIndex}>
            <div className="flex justify-between items-start mb-4">
              <h2 className="text-lg font-semibold text-balance flex-1">{currentQuestion?.question}</h2>
              {currentQuestion?.difficulty && (
                <Badge variant="outline" className="ml-3">
                  {currentQuestion.difficulty}
                </Badge>
              )}
            </div>

            <div className="grid gap-3">
              {currentQuestion?.options.map((option, index) => (
                <Button
                  key={index}
                  variant={selectedAnswer === index ? "default" : "outline"}
                  className="justify-start text-left h-auto p-4 text-wrap transition-all duration-200 hover:scale-[1.02] active:scale-[0.98]"
                  onClick={() => selectAnswer(index)}
                  aria-label={`Option ${String.fromCharCode(65 + index)}: ${option}`}
                >
                  <span className="font-medium mr-3">{String.fromCharCode(65 + index)}.</span>
                  {option}
                </Button>
              ))}
            </div>
          </div>

          <div className="flex justify-between">
            <Button
              variant="outline"
              onClick={previousQuestion}
              disabled={quizState.currentQuestionIndex === 0}
              aria-label="Previous question"
            >
              Previous
            </Button>

            <Button
              onClick={nextQuestion}
              disabled={selectedAnswer === null}
              className="flex items-center gap-2"
              aria-label={
                quizState.currentQuestionIndex === quizState.questions.length - 1 ? "Finish quiz" : "Next question"
              }
            >
              {quizState.currentQuestionIndex === quizState.questions.length - 1 ? "Finish" : "Next"}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}
